import React, { Component } from "react";
import axios from 'axios'; 
import SearchResults from './SearchResults'

const key = 'b71661f27a7080f087d50221b686a16e4549cebfd6d9edc870065558542e9ba9'
const secret = '6be7c485cd661ad6188b3889f107da5a4afec6510539c73647232bb682fb2b53'

class SearchBar extends Component{



    state={
        term:'',
        searchResult:[]
    };

    handleChange = (e) =>{
        this.setState({
            term: e.target.term
        })
    }

    onFormSubmit(event){
        event.preventDefault();
        const searchValue = this.state.term;

        axios.get(`https://api.unsplash.com/search/photos/?client_id=${key}&per_page=15&orientation=landscape&query=${searchValue}}`).then(res => {
             this.setState({

                 searchResult: res.data.results
             })     
            })
    }

    render(){
        return(
            <div className='ui segment'>
                <form onSubmit={this.onFormSubmit} className='ui form'>
                    <div className='field'>
                    <label>Image Search</label>
                     <input className='field container'
                            type ='text' 
                            value ={this.state.term}
                            onChange={this.handleChange}/>
                     </div>
                </form>
                <SearchResults results={this.state.searchResult}/>
            </div>
        )
    }
} 


export default SearchBar;
